import { Component } from '@angular/core';

@Component({
  selector: 'app-contact-us',
  templateUrl: './contact-us.component.html',
  styleUrls: ['./contact-us.component.css']
})
export class ContactUsComponent {
  onSubmit() {
    // Handle form submission logic here
    // For example, you can send the data to your backend or display a message
    alert('Your message has been sent! We will get back to you shortly.');
  }
}
<!-- contact-us.component.html -->
<div class="contact-us-container">
  <h1>Contact Us</h1>
  <p>If you have any questions, feel free to reach out to us!</p>
  <form (ngSubmit)="onSubmit()" #contactForm="ngForm">
    <div class="form-group">
      <label for="name">Name</label>
      <input type="text" id="name" name="name" required ngModel>
    </div>

    <div class="form-group">
      <label for="email">Email</label>
      <input type="email" id="email" name="email" required ngModel>
    </div>

    <div class="form-group">
      <label for="message">Message</label>
      <textarea id="message" name="message" rows="4" required ngModel></textarea>
    </div>

    <button type="submit" class="submit-btn">Send Message</button>
  </form>
</div>
