import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent {
  isSidebarOpen = false;

  toggleSidebar() {
    this.isSidebarOpen = !this.isSidebarOpen;
  }
}
<div class="sidenav" [class.open]="isSidebarOpen">
  <a href="javascript:void(0)" class="closebtn" (click)="toggleSidebar()">&times;</a>
  <a routerLink="/dashboard" routerLinkActive="active">Dashboard</a>
  <a routerLink="/shops" routerLinkActive="active">Shops</a>
  <a routerLink="/orders" routerLinkActive="active">Orders</a>
  <a routerLink="/products" routerLinkActive="active">Products</a>
  <a routerLink="/customers" routerLinkActive="active">Customers</a>
  <a routerLink="/settings" routerLinkActive="active">Settings</a>
</div>

<!-- Main content -->
<div id="main-content" [class.sidenav-open]="isSidebarOpen">
  <button class="openbtn" (click)="toggleSidebar()">&#9776;</button>
  <router-outlet></router-outlet>
</div>
